$(document).ready(()=>a());function a(){let A=document.querySelector('#hr');let b=document.querySelector('#mn');let c=document.querySelector('#sc');setInterval(()=>{let _=new Date();let B=_.getHours()*30;let C=_.getMinutes()*6;let d=_.getSeconds()*6;A.style.transform=`rotateZ(${B+C/12}deg)`;b.style.transform=`rotateZ(${C}deg)`;c.style.transform=`rotateZ(${d}deg)`;let h=new Date().getHours();let m=new Date().getMinutes();let s=new Date().getSeconds();h>12&&(h=h-12);h<10&&(h=`0${h}`);m<10&&(m=`0${m}`);s<10&&(s=`0${s}`);hours.innerHTML=h;minutes.innerHTML=m;seconds.innerHTML=s;ampm.innerHTML=h>=12?'PM':'AM'})}
